sys_script_include.script:
var updateSetName = Class.create();
updateSetName.prototype = {
    initialize: function() {},
    //deloitte_<Product>_<ScopeName>_<StroyNumber>_<DevInitials>_<Version#>
    generateUpdateSetName: function() {
        return 'deloitte_<Product>_' + this._getScope() + '_<StroyNumber>_' + this._getUserInitial() + '_<Version#>';
    },
    _getScope: function() {
        var appId = gs.getCurrentApplicationId();
        var app = new GlideRecord('sys_scope');
        app.get(appId);
        return app.scope;
    },
    _getUserInitial: function() {
        var name = gs.getUserDisplayName();
        var fullName = name.split(' ');
        var initials = fullName.shift().charAt(0) + fullName.pop().charAt(0);
        return initials.toUpperCase();
    },
    type: 'updateSetName'
};
